(function ($, window, undefined) {
	$.fn.marqueeify = function (options) {
		var settings = $.extend(
			{
				horizontal: true,
				vertical: true,
				speed: 100, // In pixels per second
				container: $(this).parent(),
				bumpEdge: function () {},
			},
			options
		);

		return this.each(function () {
			var containerWidth,
				containerHeight,
				elWidth,
				elHeight,
				move,
				getSizes,
				$el = $(this);

			getSizes = function () {
				containerWidth = settings.container.outerWidth();
				containerHeight = settings.container.outerHeight();
				elWidth = $el.outerWidth();
				elHeight = $el.outerHeight();
			};

			move = {
				right: function () {
					$el.animate(
						{ left: containerWidth - elWidth },
						{
							duration: (containerWidth / settings.speed) * 1000,
							queue: false,
							easing: "linear",
							complete: function () {
								settings.bumpEdge();
								move.left();
							},
						}
					);
				},
				left: function () {
					$el.animate(
						{ left: 0 },
						{
							duration: (containerWidth / settings.speed) * 1000,
							queue: false,
							easing: "linear",
							complete: function () {
								settings.bumpEdge();
								move.right();
							},
						}
					);
				},
				down: function () {
					$el.animate(
						{ top: containerHeight - elHeight },
						{
							duration: (containerHeight / settings.speed) * 1000,
							queue: false,
							easing: "linear",
							complete: function () {
								settings.bumpEdge();
								move.up();
							},
						}
					);
				},
				up: function () {
					$el.animate(
						{ top: 0 },
						{
							duration: (containerHeight / settings.speed) * 1000,
							queue: false,
							easing: "linear",
							complete: function () {
								settings.bumpEdge();
								move.down();
							},
						}
					);
				},
			};

			getSizes();

			if (settings.horizontal) {
				move.right();
			}
			if (settings.vertical) {
				move.down();
			}

			// Make that shit responsive!
			$(window).resize(function () {
				getSizes();
			});
		});
	};
})(jQuery, window);

$(document).ready(function () {
	$(".marquee").marqueeify({
		speed: 300,
		bumpEdge: function () {
			// Choose a random SVG from the array
			var newSvg = svgs[Math.floor(Math.random() * svgs.length)];

			// Replace the current SVG with the new one
			$(".marquee").html(newSvg);

			// Apply the new color after the SVG has been replaced
			var newColor = "hsl(" + Math.floor(Math.random() * 360) + ", 100%, 50%)";
			$(".marquee .logo").css("fill", newColor);
		},
	});
});

